/*
    Not working in latest Safari! Try Chrome or something instead.
*/


float random(vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float saturate(float x) {
    return clamp(x, 0.0, 1.0);
}

vec3 get_col(vec2 uv)
{
     return 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = get_col(uv);

    // Adjust offset and scale
    float scale = 0.1;
    vec2 offset = iResolution.xy * 0.5;
    offset.x -= iTime * 100.0;
    offset.y += sin(iTime) * 100.0;
    vec2 coord = fragCoord - offset;
    coord *= scale;

    // Get x and y as integers
    int x = abs(int(coord.x));
    int y = abs(int(coord.y));
    vec2 icoord = vec2(x,y);

    // Sparkle
    float sparkle = random(icoord + iTime * 0.00001);
    sparkle = saturate(sparkle - 0.2);
    col = mix(get_col(uv + sparkle * 1.0), vec3(.0,.0,.0), sparkle);

    // Ripple
    float ripple = texture(iChannel0, coord * 0.001 + iTime * 0.0001).r;
    ripple *= texture(iChannel0, coord * 0.001 + iTime * 0.0001 * vec2(-.9,-1.)).r;
    col *= saturate(ripple + 0.5);

    // Magic alien formulae
    if((x ^ y) % 9 == 1)
    {
        fragColor = vec4(col,1.0);
    }
    else
    {
        col = get_col(uv) * ripple * 0.1;
        fragColor = vec4(col,1.0);
    }
}

