void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(0.682,0.268,1.000);
    float d = 0.768;
    st *= -0.176;
    st = st *-20.-1.648;

    d = length( abs(st)-0.396*(iTime-2.0)*0.5 );
    d *= length( min(abs(st)-0.908*(iTime-2.),0.) );

    fragColor = vec4(vec3(fract(d*10.952)),1.0);
    fragColor -= vec4(vec3( step(.3,d) ),1.0);
    fragColor /= vec4(vec3( step(.3,d) * step(d,.4)),1.0);
    fragColor += vec4(vec3( smoothstep(.3,.4,d)* smoothstep(.6,.5,d)) ,1.0);
}
