//makes a circle from UV , p: position, r : radius
float Circle(vec2 uv, vec2 p, float r, float blur){
    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;  // from 0 to 1 -> -0.5 to 0.5 

    uv.x *= iResolution.x / iResolution.y; //aspect raio of the screen

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col = vec3(0.);

   //---face---
    float mask = Circle(uv, vec2(0,0), .4, .02);
    mask -= Circle(uv, vec2(-.15,.15), .08, .02);
    mask -= Circle(uv, vec2(.15,.15), .08, .02);

    //---mouth----
    float mouth = Circle(uv, vec2(0,0), .3, .02); 
    mouth -= Circle(uv, vec2(0,0.05), .3, .02); 
    //col = vec3(mouth);
    mask -= mouth;

    //---left ear
    float ear = Circle(uv, vec2(-0.39, 0.04), .15, .02); 
     ear += Circle(uv, vec2(0.39, 0.04), .15, .02); 


    mask += ear; 

    col = vec3(1., 1., 0) * mask;


    fragColor = vec4(col,1.);
}
