// random function
#define rand(n) fract(cos(n*89.42)*343.42)

//draw a even nicer ring
#define nice_ring(U,P,r,w)   w / abs(length(P - U) - r)

// generic rotation by angle
#define rot(a) mat2( cos(a), -sin(a), sin(a), cos(a) )


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 R = iResolution.xy;
   	vec2 uv = ( 2.* fragCoord.xy - R.xy  ) / R.y;

	float t = iTime*.3 + 123. , s = 0., r = 0.1, w = .22;

	// make the creature rotate at all
	uv *= rot(sin(t*1.3)*.3);

    // make it move over the screen
     uv.x += sin(t*.6)*.3;
     uv.y += sin(t*.3)*.2;

	// sinus mirror
	 uv.x *= sin((uv.x)*1.);
	uv.x *=1.25; // against the x stretching a bit

	// rotate within axis
	uv *= rot(t*.2);

	float deform_amplitude = sin(t*.9)*.8+.2;
	float deform_freq = sin(t*.6)*.8+.2;


    for(float i=0.;i<16.;i++){

		vec2 posi = vec2(sin(t*i*.156 + i*12.2423)*.3 , sin(i*12.45313 + t*.132*i)*.3 );

		 uv.x += sin(uv.y*6.*deform_freq + t*.6)*.01*deform_amplitude;
		 uv.y += sin(uv.x*6.*deform_freq + t*.3)*.01*deform_amplitude;

		s += nice_ring( uv, posi, r,w*.01 );

		// shrink stuff
		 r +=.025 ;
		 w +=.05 - i*.002;

	}

	s = smoothstep(1.,.3,s)*.8-.2;

 	// lowkey dithering - very light for nice fullscreen grain
	s -= rand(uv.x)*.02; 

	// tweaky vignette whatever  
	float vig = uv.x*uv.y * 10.0; 
	s += vig*.01;

	fragColor = vec4( vec3(s), 1.0 );


}
