void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy / iResolution.x;

    int cnt = 10;
    int[] remap = int[](3,6,2,4,1,5);
    float[] coef = float[](0.3,0.7,1.3,5.3,3.1,0.9);
    vec2 uv = fragCoord/iResolution.x;
    float cells = 100.0;
    int i = int(floor(uv.x * cells));
    int j = int(floor(uv.y * cells));
    int c = remap[(i + j) % cnt];
    float t = iTime / cos(iTime / 100.0) / 5.0;
    float r = sin(coef[c] * t + float(i+j)) / 2.0 + 1.0;
    float g = cos(coef[c] * t * float(i) + float(c)) / 2.0 + 1.0;
    float b = sin(coef[c] * t + float(j)) / 2.0 + 1.0;

    vec3 col = vec3(r,g,b);

    fragColor = vec4(col,1.0);
}
