#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<90.;
        O.xyz+=.03*H(log(s)*.3+.5)*exp(-8.*i*i*e)
    )
    {
        p=g*d-vec3(-.2,.3,1.2);
        p=R(p,normalize(vec3(1,2.*sin(iTime*.1),3)),iTime*.2);
        s=2.;
        for(int j=0;j++<10;s*=e)
            p=sign(p)*(1.-abs(abs(p-2.)-1.)),
            p=p*(e=6./clamp(dot(p,p),.1,3.))-vec3(8,.2,8);
        g+=e=length(p)/s;}
}
