vec3 randomBar(vec2 uv, float intensity)
{

    vec3 col = vec3(0.0);
    vec3 random = texture(iChannel1, uv).rgb;


    if ( random.x < intensity) {
        col += vec3(1,0,0);
    }
    if ( random.y < intensity) {
        col += vec3(0,1,0);
    }   
    if ( random.z < intensity) {
        col += vec3(0,0,1);
    }   

    return col;
}

float curve(float x)
{
    return (0.5 + 0.5 * sin( x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;



       float intensity = uv.y;
       vec2 bar = uv.yy;
       float colorMult= 1.0;

    if ( uv.x > uv.y * (1. - 0.2 * curve(9. * uv.x)))
    {

       intensity = uv.x ;
        bar = uv.xx;

    }

    if ( uv.x > (2.0 - 0.4 + 0.5 * curve(2. * uv.x)) * uv.y )
    {

       intensity = uv.x ;
        bar = uv.xx + 0.1;
        colorMult = 0.1;

    }

   if ( uv.x >  (3.0 + 0.4 * curve(10. * uv.x)) * uv.y )
    {

       intensity = uv.x ;
        bar = uv.xx - 0.1;
        colorMult = 0.8;
    }



    vec3 col = texture(iChannel0, uv).rgb * colorMult;


    float lineGlitch = (1. - intensity) * (0.5 + 0.5 * sin(0.5 * iTime));

    float blackGlitch = 1.0 + intensity * (0.5 + 0.5 * sin(iTime* 0.3));

    col += randomBar(bar, lineGlitch);

    vec3 random = texture(iChannel1, bar).rgb;

    if ( length(random) > blackGlitch) {
       col = vec3(0,0,0);

      // col = texture(iChannel0, vec2(0.5, uv.y)).rgb;
    }  

     if ( uv.x > 0.9 )
    {

       intensity = uv.y ;
        bar = uv.yy - 0.1; 
        col *= 0.6;
        col += 0.1 * randomBar(bar, (0.5 + 0.5 * sin(0.5 * iTime) ));

    }


     if ( uv.x > 0.2  && uv.x < 0.25 )
    {

       intensity = uv.y ;
        bar = uv.yy - 0.9; 
        //col += 0.5 * randomBar(bar/2., (0.5 + 0.5 * sin(0.5 * iTime)));
          col = texture(iChannel0, vec2(0.5, uv.y)).rgb * colorMult;

    }


    fragColor = vec4(col,1.0);
}
