void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * aspect;
    uv *= 1.4;
    float r0 = 0.13, r1 = 0.4;
    float t = iTime;
    float r = length(uv);
    float phi = atan(-uv.y, -uv.x);
    r += 0.05 * sin(r * 12.0 + phi + t * 3.0);
    float p1 = phi * 0.15915494309189535;
    float d0 = sin(phi + 0.7 * sin(phi * 2. + 0.3 + 0.1 * r + t) - 0.8 - .73 * t);
    float dd = .5 + .5 * d0;
    float a = smoothstep(r1, 0.0, abs(r * r - r0) + .06 * (1.5 - 1.5 * d0));
    a *= smoothstep(0.1, r0 * 2.5, r);
    r += .0164 * pow(2. + sin(77. * phi + r * r * 226. - 5.1 * t) * dd * a
       + sin(55. * phi + r * r * 156. + 7.1 * t) * dd * a, 2.0);
    float e = smoothstep(r1, 0.0, abs(r * r - r0) + .06 * (1.5 - 1.5 * d0));
    e *= smoothstep(0.1, r0 * 2.5, r);
    a = mix(a, e, .5 * smoothstep(.5, 1., a * a));
    a = mix(a, sqrt(a), .5 + .5 * d0);
    float b = clamp(2.*a - 1.,0.,1.);
    b *= b; b *= b; b *= b;
    vec3 col = vec3(2.*a - a * a, a * a * a, b);

    // Output to screen
    fragColor = vec4(pow(col, vec3(1.)),1.0);
}
