//iChannel0 is the static water background, usually a blue image
//iChannel1 is the foam texture, usually a image with most space black and some white line
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 1.0;
    vec2 waveUv1 = uv + iTime * vec2(0.02, 0.02);
    vec2 waveUv2 = uv + 0.3 * vec2(-0.03, -0.015);
    vec3 waveCol1 = texture(iChannel1, waveUv1).rgb;
    vec3 waveCol2 = texture(iChannel1, waveUv2).rgb;
    vec3 waveCol = waveCol2 * waveCol1.r + waveCol1 * (1.0 - waveCol1.r);
    vec3 mainCol = texture(iChannel0, uv * 0.2).rgb;
    vec3 mainColWithWave = min(vec3(1.0, 1.0, 1.0), mainCol + waveCol);
    vec3 col = /*waveCol1 * (1.0 - waveCol1.r) */mainColWithWave * waveCol2.r + mainCol * (1.0 - waveCol2.r);
    // Output to screen
    fragColor = vec4(col,1.0);
}
