// Fork of "Transport Dynamics II" by wyatt. https://shadertoy.com/view/sdl3RN
// 2021-03-14 01:41:52

// Display ðŸŒµ
Main 
{
    vec4 f = A(U), c = B(U);
    Q = (C(c.xy*R)*1.6-.6)*min(f.w,1.3);
    Q *= 1.6+sqrt(Q);
}

// Forces ðŸ¦œ
Main
{
    Q = A(U);
    vec4 q = C(B(U).xy*R)*2.-1.;
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        vec4 b = B(U+u);
        vec4 c = C(b.xy*R)*2.-1.;
        Q.xy -= 0.1*Q.w*a.w*(a.w+.25*dot(c,q)-1.)*u;  
    }
    Q.y -= 1e-2*q.x;
    Q.xy *= .99;
    if (iMouse.z>0.) 
    Q.xy += -1e-3*(iMouse.xy-U)/(1.+length(iMouse.xy-U));
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    if (iFrame < 1)Q = vec4(0,0,0.,.5);
    if (U.x < 4.||R.x-U.x<4.) Q.xy *= 0.;
    if (U.y < 4.||R.y-U.y<4.) Q.xy *= 0.;
    if (Q.w>2.) Q.w = 2.;
}

// Advect ðŸ¿
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float j = .5+.5*max(1.-4.*Q.w*q.w,0.);
       float k = .5+.5*max(1.-4.*Q.w*q.w,0.);
       float wa = 0.25*Q.w*min(i,j)/j;
       float wb = 0.25*q.w*max(k+i-1.,0.)/k;
        dQ.xyz += Q.xyz*wa+q.xyz*wb;
        dQ.w += wa+wb;

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;

    if (U.x < 4.||R.x-U.x<4.) Q.xy *= 0.;
    if (U.y < 4.||R.y-U.y<4.) Q.xy *= 0.;
}

// Advect ðŸ¿
Main
{
    Q = A(U);
    vec4 Qb = B(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u),
             qb = B(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float j = .5;
       float k = .5;
       float wa = 0.25*Q.w*min(i,j)/j;
       float wb = 0.25*q.w*max(k+i-1.,0.)/k;
        dQ.xyz += Qb.xyz*wa+qb.xyz*wb;
        dQ.w += wa+wb;

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;
    if (iFrame<1)
        Q = vec4(U/R,1.-(U.x+U.y)/R.x,1);
}
