vec2 hash22(vec2 p) {
    p = vec2( dot(p,vec2(2127.1,81.17)), dot(p,vec2(1269.5,283.37)) );
    return fract(sin(p)*43758.5453);
}


float perlinNoise(vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );
    //vec2 u = f*f*(3.0-2.0*f);//插值函数
    vec2 u =f*f*f*(6.0*f*f-15.0*f+10.0);
    float n = mix( mix( dot( -1.0+2.0*hash22( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                        dot( -1.0+2.0*hash22( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                   mix( dot( -1.0+2.0*hash22( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                        dot( -1.0+2.0*hash22( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
    return n;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float n=perlinNoise(uv*15.0);
    vec3 orignCol=texture(iChannel0,uv).rgb;
    vec3 col0=vec3(0.23,0.95,0.1);
    float t=mod(iTime*0.2,1.25);
    float x=uv.x+n*0.1+0.75-t;
    float d=(max(abs(x-0.5)*5.0-0.1,0.0))*step(x,0.525)+step(0.525,x)*0.02;
    d=d*20.0;
    vec3 col=orignCol+(col0/d); 
    col*=step(uv.x,t);
    fragColor = vec4(col,1.0);
}
