// inspired from https://shadertoy.com/view/NdlSDB
// acid test simplification of https://shadertoy.com/view/WlyBR3

void mainImage( out vec4 O, vec2 U )
{
    U -= .5 + iResolution.xy/2.;

    float t = mod(iTime,10.);
                                             // just alternates zoom and  translate in random dir 
    if ( t < 5. ) U += floor(30.*t) * sign( cos( 6.28*fract(1e4*sin(1e3*floor(iTime/10.))) +vec2(0,11) ));
       else       U *= exp(-.1*floor(30.*(t-5.)));

    U *= .01;
    float a = U.x , b = U.y;   

    O = vec4( abs( (1.+a)*(1.+b)-(1.+a+b) - a*b ) < 1e-10 );  // (1.+a)*(1.+b)-(1.+a+b) = a*b, with precision error
 // O = vec4( abs( a*b - a*b ) < 1e-10 );    // this one is way more disturbing 
}
