vec2 c_add(vec2 c1, vec2 c2){
    return vec2(c1.x + c2.x, c1.y + c2.y);
}

vec2 c_sq(vec2 c){
    return vec2(c.x * c.x - c.y * c.y, 2.0 * c.x * c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 4.0 - 2.0;
    uv *= vec2(1.7777, 1);

    vec2 c = vec2(iMouse.x * 0.001, iMouse.y * 0.001);
    vec2 z = uv;

    vec3 color;
    float max_iteration = 50.0;

    for(float iteration = 0.0; iteration < max_iteration; iteration ++){
        // f(z) = z^2 + c
        z = c_add(c_sq(z), c);

        if(length(z) > 4.0){
            float speed = iteration / max_iteration;
            color = mix(vec3(0.1, 0.11, 0.2), vec3(0.6, 0.7, 0.9), speed);
        }
    }


    fragColor = vec4(color,1.0);
}
