#define res resolution
#define ft float
#define aaa 40.*(iMouse.y/iResolution.y)
#define dd 0.009

ft rnd(vec2 v, ft t, ft m){
    ft aa = 12.*sin(t*20.)+15.;
    return fract(sin(aa*fract(t+m*dot(v+t,fract(v*79.17)))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy/iResolution.y*.999;
    uv *= .8;
    ft t = iTime*.05;
    ft m = 4.;

    vec2 d1 = vec2(0.,dd);
    vec2 d2 = vec2(dd, 0.);
    vec2 d3 = vec2(dd);    
    vec2 d4 = vec2(dd, -dd);


    ft r = rnd(uv, fract(t), m);
    r *= rnd(uv*.9, fract(t+5.), m);

    ft r1 = rnd((uv-d1), fract(t), m);
    r1 *= rnd((uv-d1)*.9, fract(t+5.), m);

    ft r2 = rnd((uv+d1), fract(t), m);
    r2 *= rnd((uv+d1)*.9, fract(t+5.), m);

    ft r3 = rnd((uv-d2), fract(t), m);
    r3 *= rnd((uv-d2)*.9, fract(t+5.), m);

    ft r4 = rnd((uv+d2), fract(t), m);
    r4 *= rnd((uv+d2)*.9, fract(t+5.), m);

    ft r5 = rnd((uv-d3), fract(t), m);
    r5 *= rnd((uv-d3)*.9, fract(t+5.), m);

    ft r6 = rnd((uv+d3), fract(t), m);
    r6 *= rnd((uv+d3)*.9, fract(t+5.), m);

//    ft r7 = rnd((uv-d4), fract(t), m);
//    r7 *= rnd((uv-d4)*.9, fract(t+5.), m);

//    ft r8 = rnd((uv+d4), fract(t), m);
//    r8 *= rnd((uv+d4)*.9, fract(t+5.), m);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    ft f =  r+r1 + r2 + r3 + r4 + r5 + r6;
    f *= .22;
    vec3 c = f*6.28+vec3(3.,5.,4.);
    c = cos(c+5.)*.5+.5;
    fragColor = vec4( vec3(c*(.4+.6*col)), 1.0 );
    fragColor.y *= .4;

}
