#define R iResolution.xy

vec2 cMul(vec2 a, vec2 b) { return vec2(a.x*b.x-a.y*b.y, a.x*b.y+b.x*a.y); }
vec2 cDiv(vec2 a, vec2 b) { float dotB = 1.0/dot(b,b); return dotB * vec2(a.x*b.x+a.y*b.y, a.y*b.x-a.x*b.y); }

vec2 mobius(vec2 z, vec2 a, vec2 b, vec2 c, vec2 d) 
{ 
    return cDiv(cMul(a, z) + b, cMul(c, z) + d); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 0.025 * iTime;
    vec2 uv = (2.0*fragCoord-R)/R.y;
    vec2 mouse = (2.0*iMouse.xy-R)/R.y;
    vec2 I = vec2(1, 0);
    vec2 J = vec2(0, 1);

    uv *= 0.5;

    vec2 a = cos(3.*t)*I;
    vec2 b = sin(5.0*t)*J + vec2(0.1);
    vec2 c = sin(7.0*t)*4.*J + vec2(0.1);
    vec2 d = cos(11.0*t)*I;
    uv = mobius(uv, a, b, c, d);

    vec3 col = texture(iChannel0, fract(vec2(1./6., 1.0)*uv*10.0)).rgb;

    fragColor = vec4(col,1.0);
}
