vec3 heatPalette(in float h) {
    //return vec3(1.0, pow(0.95, h), pow(0.1, h)) * pow(0.98, h);
    return pow(vec3(0.98, 0.93, 0.1), vec3(h));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float h = fragCoord.x / iResolution.x * 100.0;
    fragColor = vec4(heatPalette(h), 1.0);
}
