const float PI = 3.141592653589793238462643;

float random(const vec2 st);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //vec2 squareOrigin = vec2(vel.x*mod((iTime/10.0), 1.0), vel.y*mod((iTime/10.0), 1.0));
    vec2 squareOrigin = vec2(.4*sin((iTime/10.0)*PI)+.4, .3);

    vec3 col = texture(iChannel1, uv.xy).rgb;
    col.z = sin(.3*iTime);

    if(uv.x > squareOrigin.x && uv.x < squareOrigin.x+.2 && uv.y > squareOrigin.y && uv.y < squareOrigin.y+.3) {
        col = texture(iChannel0, vec2(uv.x-squareOrigin.x, mod(squareOrigin.y*iTime/10.0, 1.0))).rgb;
    }

    //creates a block wherever the mouse clicks
    vec2 mouse = iMouse.xy/iResolution.xy;
    if(uv.x > (mouse.x - .03) && uv.x < (mouse.x + .03) && uv.y > (mouse.y - .05) && uv.y < (mouse.y + .05)){
        col = vec3(.3, .3, .8);
    }
    col.xyz += mod(random(uv), .4);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float random(const vec2 st){
    return fract(sin(dot(st, vec2(12.9898, 78.233)))*43758.5453123);
}
