#define chromaticAberration true
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if (chromaticAberration) {
        float r = length(uv - vec2(0.5, 0.5));
        vec2 thetaVec = normalize(uv - vec2(0.5, 0.5));

        float bMod = r * 1.0;
        float gMod = r * 0.995;
        float rMod = r * 0.99;

        vec2 rPos = vec2(0.5,  0.5) + thetaVec * rMod;
        vec2 gPos = vec2(0.5,  0.5) + thetaVec * gMod;
        vec2 bPos = vec2(0.5,  0.5) + thetaVec * bMod;

        float red = texture(iChannel0, rPos).r;
        float green = texture(iChannel0, gPos).g;
        float blue = texture(iChannel0, bPos).b;

        vec3 col = vec3(red, green, blue);
        fragColor = vec4(col, 1.0);
    } else {
        fragColor = texture(iChannel0, uv);
    }
}

#define C(i, j) texture(iChannel0, uv + vec2(i, j) / iResolution.xy).x
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = vec4(0.0);
    if(iMouse.w > 0.0 || iFrame == 0) {
        fragColor = rand(vec2(uv.x+iTime, uv.y)) > 0.8 && abs(length(uv-vec2(0.5, 0.5))) < 0.1 ? fragColor = vec4(1.0) : vec4(0.0);
    } else {
        vec3 preVal = texture(iChannel0, uv).xyz;
        if(preVal.x > 0.5) { //alive
            fragColor = vec4(0.0, 0.0, 1.0, 1.0);
        } else if (preVal.z < 0.5) {
            fragColor = int(C(-1.0, -1.0) + C(0.0, -1.0) + C(1.0, -1.0) + C(-1.0, 0.0) + C(1.0, 0.0) + C(-1.0, 1.0) + C(0.0, 1.0) + C(1.0, 1.0)) == 2 ? vec4(1.0) : vec4(0.0);
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = mix(texture(iChannel0, uv), texture(iChannel1, uv), 0.7) * 1.2;
    //Adding a tiny bit of blur just for fun, creates a sort of trail effect behind gliders

