// Ported https://github.com/szabolcsdombi/zengl/blob/main/examples/julia_fractal.py

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int iter = 100;
    vec2 z = vec2(5.0, 3.0) * (gl_FragCoord.xy / iResolution.xy - 0.5);
    vec2 c = iMouse.xy / iResolution.xy * 2.0 - 1.0;
    int i;
    for(i = 0; i < iter; i++) {
        vec2 v = vec2(
            (z.x * z.x - z.y * z.y) + c.x,
            (z.y * z.x + z.x * z.y) + c.y
        );
        if (dot(v, v) > 4.0) break;
        z = v;
    }
    float cm = fract((i == iter ? 0.0 : float(i)) * 10.0 / float(iter));
    fragColor = vec4(
        fract(cm + 0.0 / 3.0),
        fract(cm + 1.0 / 3.0),
        fract(cm + 2.0 / 3.0),
        1.0
    );
}
