float drawLight(vec2 coord, vec2 pos, float radius, float intensity)
{
    return max(0.0, (radius - length(coord - pos)) * intensity);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float centerLight = drawLight(uv, vec2(0,0), 0.3, 2.0);
    float mouseLight = drawLight(uv, mouse, 0.4, 2.0);    

    fragColor = vec4(mouseLight + centerLight,0,0,1);
}
