mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
float diam(vec3 p,float s){
     p = abs(p);
     return (p.x+p.y+p.z-s)*inversesqrt(3.);
}
vec2 sdf(vec3 p){
    p.xz*=rot(iTime*.1);
    vec2 h;
    h.x = 1000.+length(p)-1.;
    vec4 pp = vec4(p,1.);
    float s = 0.;
    for(float i=0.;i<=9.;i++){
        pp.xyz = abs(pp.xyz)-9.95;;
        pp*=1.7;
        pp.xz*=rot(.785+i);
        pp.xy*=rot(-.785+i);
        float q = (diam(pp.xyz,1.95))/pp.a;
        h.x = mod(i,2.)==0. ? mix(q,h.x,0.75): min(q,h.x);
        s+=clamp(cos(pp.y)+sin(pp.x),0.,1.);
    }
    s/=pp.a*1.8;
    h.x = min(length(p)-1.-s,h.x);
    h.y = 1.;
    return h;
}

#define q(s) s*sdf(p+s).x
#define ao(rp,n,k) (sdf(rp+n*k).x/k)
#define gao(rp,n) (ao(rp,n,.1)+ao(rp,n,.3)+ao(rp,n,.7))
vec3 norm(vec3 p,float ee){ vec2 e=vec2(-ee,ee) ; return normalize(q(e.xyy)+q(e.yxy)+q(e.yyx)+q(e.xxx));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    uv*=.5;
    vec3 ro = vec3(0.,0.,-5.);
    vec3 rp = ro;
    vec3 rd = normalize(vec3(uv,1.));
    vec3 col = vec3(.1);
    vec3 light = vec3(1.,2.,-3.);
    for(float i=0.;i<=128.;i++){
        vec2 d = sdf(rp);
        if(d.x <=.001){
            vec3 n = norm(rp,.0003);
            float diff =max(0.,dot(normalize(light-rp),n));
            col = vec3(1.)*diff;
            col = mix(vec3(.1),col,gao(rp,n)/2.);
            break;

        }
        rp+=rd*d.x;

    }

    fragColor = vec4(col,1.0);
}
