void mainImage( out vec4 O, in vec2 C )
{
    O = texelFetch(iChannel0,ivec2(C),0);
}

void mainImage( out vec4 O, in vec2 C )
{
    //sort x
    ivec2 id=ivec2(C);//C-=.5;
    int isEven=iFrame%2;
    float isR=float((id.x+isEven)%2);
    float dir=1.-2.*step(.5,isR);
    vec4 lData=texelFetch(iChannel0,mapCoord(C,vec2(isR*dir,0.),iR),0),
         rData=texelFetch(iChannel0,mapCoord(C,vec2((1.-isR)*dir,0.),iR),0),
         sData=mix(lData,rData,isR),
         tData=mix(rData,lData,isR);
    O.rgb=length(lData.x)>length(rData.x)?tData.xyz:sData.xyz;

#ifdef SORT_RANDOM_COLOR
    O.rgb=iFrame<10?randomVec3(C):O.rgb;
#endif
#ifdef SORT_TEX
    O.rgb=iFrame<10?texture(iChannel1,C/iR).xyz:O.rgb;
#endif
#ifdef SORT_TEX2
    O.rgb=iFrame<10?texture(iChannel2,C/iR).xyz:O.rgb;
#endif
}

/*--------exp settings--------*/
//#define SORT_RANDOM_COLOR
#define SORT_TEX
//#define SORT_TEX2
/*--------exp settings--------*/

#define iR iResolution.xy
#define iT iTime

vec3 randomVec3(vec2 seed)
{
    uint x=uint(seed.x),y=uint(seed.y),z;
    x*=uint(867);
    y*=uint(923);
    x^=x<<11^x>>8;
    y*=x;
    y^=y<<16^y>>6;
    x=y*(x<<11^x>>6^x);

    y=x<<13^y>>6;
    z=y<<12^y>>7;
    return vec3(x,y,z)/4294967297.;
}

ivec2 mapCoord(vec2 c,vec2 dir,vec2 resol)
{
    ivec2 nc=ivec2(c+dir);
    ivec2 resolution=ivec2(resol);
    if(nc.x>=resolution.x||nc.x<0||
        nc.y>=resolution.y||nc.y<0)
        return ivec2(c);
    return nc;
}

void mainImage( out vec4 O, in vec2 C )
{
    //sort y
    ivec2 id=ivec2(C);//C-=.5;
    int isEven=iFrame%2;
    float isU=float((id.y+isEven)%2);
    float dir=1.-2.*step(.5,isU);
    vec4 dData=texelFetch(iChannel0,mapCoord(C,vec2(0.,isU*dir),iR),0),
         uData=texelFetch(iChannel0,mapCoord(C,vec2(0.,(1.-isU)*dir),iR),0),
         sData=mix(dData,uData,isU),
         tData=mix(uData,dData,isU);
    O.rgb=length(dData.y)>length(uData.y)?tData.xyz:sData.xyz;

}
