const float zoom = .8;
const vec2 shift = vec2(0.0,0.088);
const vec2 flower = vec2(
    1.0, // arm spins
    -2.0 // hand spins
);

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x -= (iResolution.x - iResolution.y) / iResolution.x * 0.5;
    uv.x *= iResolution.x / iResolution.y;

    uv -= 0.5;

    uv *= zoom;

    uv += shift;

    float bw = 1.0;

    for (int i=0;i<1000;i++){

        vec2 center = vec2(0.,0.2);

        center = rotate(center, iTime + float(i) * 0.0407) * flower.x;
        center += rotate(vec2(0.,0.2), (iTime + float(i) * 0.0407) * flower.y);

        bw *= smoothstep(.03,.031, distance(uv,center));

    }

    // Output to screen
    fragColor = vec4(vec3(bw),1.0);
}
