const float PI = 3.14,
    w = 0.4,
    h = 0.4,
    lw = 0.7,
    lh = 0.7,
    R = 0.02;
const vec2 c = vec2(0.5, -0.1);

vec2 rotZ(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c) * p;
}

vec4 drawRect(float w, float h, vec2 U, float d, float ax, float ay, float s) {
    vec4 o = vec4(1.);
    vec2 rU = rotZ(U, ax);
    o *= vec4(min(exp((-abs(rU.y - rU.x) + h + s * rU.x/12.)/d), 1.));
    rU = rotZ(U, ay);
    o *= vec4(min(exp((-abs(rU.y - rU.x) + w)/d), 1.));
    return o;
}

vec4 drawBall(float r, vec2 U, vec2 c, float d) {
    return vec4(min(exp((-distance(U, c) + r)/d), 1.));
}

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    O = vec4(1.);
    vec2 U2 = U - c;
    vec2 mDir = normalize((2. * iMouse.xy - Res) / Res.y);
    O *= (drawRect(lw, lh, U2, 0.005, -PI/4., PI/4., -1.)
        -(drawRect(w, h, U2, 0.005, -PI/4., PI/4., 1.)
        - drawRect(w * 0.85, h  * 0.85, U2, 0.004, -PI/4., PI/4., 1.)
        + drawBall(w/8., U2, vec2(-w/3., 0.05) + R * mDir, 0.004)
        + drawBall(w/5., U2, vec2(w/3., 0.05) + R * mDir, 0.004)));
}
