void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col;

    float r = 10.;
    vec2 tPos = vec2(400., 250.);
    float tSize = 10.;
    float tRange = 100. + sin(iTime / 2.) * 30.;
    vec2 tFwd = normalize(vec2(cos(iTime / 2.), sin(iTime / 2.)));
    float tAng = 90. + sin(iTime / 3.) * 60.;


    if(length(fragCoord-tPos) < tSize)
    {
        col = vec4(1., 0., 0., 1.);
    }
    else if(length(fragCoord-tPos) < tRange)
    {
        if(acos(dot(normalize(fragCoord-tPos), tFwd)) * 57.2957795 < tAng / 2.)
        {
            col = vec4(0., 0., 1., 1.);
        }
        else
        {
            col = vec4(0., 1., 0., 1.);
        }
    }

    if(length(fragCoord-iMouse.xy) < r)
    {
        if(length(fragCoord - tPos) < tRange && dot(normalize(fragCoord - tPos), tFwd) > cos(tAng * 0.0174532925 / 2.))
        {
            col = vec4(1., 0., 0., 1.);
        }
        else
        {
            col = vec4(1.);
        }
    }

    fragColor = vec4(col);
}
