void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float VerticalLineWidth = 0.2;
    float bars = mod(uv.x, VerticalLineWidth);
    float remap = bars * (1.0f / VerticalLineWidth); // remap from [0, VerticalLineWidth] to [0, 1]
    float camera_index = floor(remap + 0.5f); // either 0 or 1

    vec3 camera1_result = vec3(179, 255, 166) / 255.0f; // pretty green
    vec3 camera2_result = vec3(255, 166, 166) / 255.0f; // pretty red

    vec3 result = mix(camera1_result, camera2_result, camera_index); // choose camera1 result or camera2 result based on camera index

    // Output to screen
    fragColor = vec4(result, 1.0);
}
