float cc(vec2 pos, float rad) {
    return step (rad, length (pos - vec2(400.0)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy;
    vec3 color1 = vec3(0);
    vec3 color2 = vec3(0);
    float circle = cc(uv, 23.0);
    color1 = vec3(circle);
    fragColor = vec4(color1, 1.0);
}
