// 画圆
float Circle(vec2 uv, vec2 pos, float r, float blur)
{
    float color = length(uv - pos);
    color = smoothstep(r, r - blur, color); 
    return color;
}
// 画一个条
float Band(float t, float start, float end, float blur)
{
    float color = smoothstep(start, start + blur, t); 
    color *= smoothstep(end, end - blur, t);
    return color;
}
// 画矩形
float Rect(vec2 uv, vec2 pos, float width, float height, float blur)
{
    float color = Band(uv.x, pos.x, pos.x + width, blur);
    color *= Band(uv.y, pos.y - height, pos.y, blur);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    // 颜色滤镜
    vec3 lvjing = vec3(1.0, .0, .5);

    float col;
    float t = (uv.x*10.)+iTime*2.5+uv.y*3.;
    float y = sin((.4*10.)+iTime*2.5+uv.y*3.)*.03;
    float uvx = uv.x;
    float uvy = uv.y + sin(t)*.03;

    col = Rect(vec2(uvx, uvy), vec2(-.4, 0.2+y), 0.8, 0.4, 0.01);

    col *= .6+(cos(t)*1.6)*.2;
    lvjing *= col;

    fragColor = vec4(lvjing, .0);
}
