// adapted from https://www.shadertoy.com/view/MllSzj
// which was inspired by https://www.shadertoy.com/view/lllSRj

void mainImage(out vec4 o, vec2 i) { 
    vec2 uv = i / iResolution.xy * 2.;

    vec4 t = texture(iChannel1, mod(uv, 1.));
    vec4 x = (vec4(pow(t.rgb, vec3(1.5)), t.a) - 0.02 ) * 1.2;
    if (uv.y > 1.)
    {
        if (uv.x < 1.) // original
            o = t;
        else // before dither
            o = x;
    }
    else
    {
        if (uv.x < 1.) // MllSzj
            o = step(texture(iChannel0, i/8.).r, t);
        else // final
            o = step(texture(iChannel0, i/8.).r, x);
    }
}
