#define ITR 32
#define EPS 0.001
#define amp 5.0

mat2 rot(float angle){
	float s=sin(angle);
	float c=cos(angle);
	return mat2(c,-s,s,c);
}

float sdBox( vec3 p){
  vec3 b=vec3(0.5);
  vec3 q=abs(p)-b;
  return length(max(q,0.0))+min(max(q.x,max(q.y,q.z)),0.0);
}

float sdSphere(vec3 p){
	return length(p)-(cos(iTime)+1.5);
}

float displacement(vec3 p){
	float h= ((cos(amp*p.x)*sin(amp*p.y)*sin(amp*p.z))*cos(30.1));
	return h;
}

float mainDist(vec3 p){
	p.xy*=rot(iTime);
	p.xz*=rot(iTime);
	float disp=displacement(p);
	float sphere=sdSphere(p);
	float box=sdBox(p);
	return disp+sphere;
}

vec3 genNormal(vec3 p){
	return normalize(p);
}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
	vec3 ro=vec3(0.0,0.0,-16.0);
	vec3 lookat=vec3(0.0,0.0,0.0);
	float zoom=5.0;

	vec3 f=normalize(lookat-ro);
	vec3 r=normalize(cross(vec3(0.0,1.0,0.0),f));
	vec3 u=cross(f,r);
	vec3 c=ro+f*zoom;
	vec3 i=c+uv.x*r+uv.y*u;
	vec3 rd=normalize(i-ro);

    float d,t;
    vec3 p,n;

	vec3 col=vec3(1.0);

    for(int i=0;i<ITR;i++){
        p=ro+rd*t;

        d=mainDist(p);
        n=genNormal(p);
        if(d<EPS)break;
        t+=d;
    }

	col=reflect(n,n);

	fragColor=vec4(col,1.0);
}
