float saturate(float x) { return clamp(x, 0.0, 1.0); }

float rand(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy  / iResolution.xy);
    int tx = int(uv.y);
    float react = texelFetch( iChannel0, ivec2(tx,0), 0 ).x*0.05;
    float scale = iResolution.y / 100.0+iResolution.y;
    vec2 p = fragCoord.xy*.0021+ 0.005*iResolution.xy /1000.;
    vec2 p1 = fract(p*10.) - 10.5;
    vec2 p2 = fract((p - 0.5)*100.) - 0.5;
    float rnd = texture( iChannel1, vec2(p)/1.5).x;
    float rnd2 = texture( iChannel2, vec2(p)).x;
    float rnd3 = texture( iChannel3, vec2(p)).x;

    float z1 = rand(0.12 * floor(p*100.));
    float z2 = rand(0.23 * floor(p - 0.5)*10.);

    float r1 = 0.2 + 0.2 * sin(iTime * 1.9-react + z1 * 9.01*react);
    float r2 = 0.2 + 0.2 * sin(iTime * 1.9-react + z2 * 9.01*react);

    float c1 = saturate((r1 - length(p1)) * scale);
    float c2 = saturate((r2 - length(p2)) * scale);

    float a1 = saturate((r1 + 0.8 - length(p1)) * scale);
    float a2 = saturate((r2 + 0.8 - length(p2)) * scale);

    float c = mix(
        mix(mix(0.0, c1, a1), c2, a2)+rnd,
        mix(mix(0.0, c2, a2), c1, a1)+rnd,
        step(z1, z2)
    );
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,3,6));
    fragColor = vec4((col*rnd*(c, c, c)*.1+react*(c,c,c)*40.*rnd2*col)/rnd3-react, 1.0);
}
