float GetRandom(float extent, vec2 vertex)
{
    float a =  iTime + vertex.x * 1.2 + vertex.y * 17.21;
    float multiplier = fract(a);
    float b = (a * 3.0 + 1.0) * multiplier + (1.0 - multiplier) * a * 0.5f;
    multiplier = fract(b);
    a = (b * 3.0 + 1.0) * multiplier + (1.0 - multiplier) * b * 0.5f;
    return mod(a, extent);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float random = GetRandom(256.0, fragCoord) * 0.00390625/*(1/256)*/;
    fragColor = vec4(random,random,random,1.0);
}
