void mainImage( out vec4 fragColor , in vec2 fragCoord ){

    vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;

    vec4 solidRed = vec4(0.0,0.0,0.0,1.0);

    if (xy.x < 0.5 && xy.y < 0.5){

        solidRed.r = 1.0;

    }
    else{

        solidRed.r = 0.0;

    }

    fragColor = solidRed;

}
