#define OFFSETX1 2.0
#define OFFSETX2 6.0
#define OFFSETX3 10.0
#define OFFSETY1 1.0
#define OFFSETY2 5.0
#define OFFSETY3 7.0
#define OFFSETY4 8.0

#define VIDEOLENGTH 29.9

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 xy = fragCoord / iResolution.xy;
    vec4 texColor = texture(iChannel0, xy);

    xy.x = (fragCoord.x + OFFSETX1) / iResolution.x;
    vec4 texColor1 = texture(iChannel0, xy);
    xy.x = (fragCoord.x - OFFSETX1) / iResolution.x;
    vec4 texColor2 = texture(iChannel0, xy);
    xy.x = (fragCoord.x + OFFSETX2) / iResolution.x;
    vec4 texColor3 = texture(iChannel0, xy);


    float f1 = iResolution.y / VIDEOLENGTH * iChannelTime[0]; 

    if (fragCoord.x > OFFSETX3 && fragCoord.x < iResolution.x - OFFSETX3) {
        if (fragCoord.y > iResolution.y - OFFSETY1 - f1 && fragCoord.y <= iResolution.y  - f1) {
            fragColor = texColor1;
        }
        else 
            if (fragCoord.y > iResolution.y - OFFSETY2 - f1 && fragCoord.y <= iResolution.y - OFFSETY1 - f1) {
                fragColor = texColor2;
            }
            else
                if (fragCoord.y > iResolution.y - OFFSETY3 - f1 && fragCoord.y <= iResolution.y - OFFSETY2 - f1) {
                    fragColor = texColor3;
                }
                else
                    if (fragCoord.y > iResolution.y - OFFSETY4 - f1 && fragCoord.y <= iResolution.y - OFFSETY3 - f1) {
                        fragColor = texColor1;
                    }
                    else
                        fragColor = texColor;
    }
    else
        fragColor = texColor;
}
