#define VIDEOLENGTH 29.9
#define OFFSETX 10.0
#define OFFSETY 8.0

float random (float y) {
    float val = sin(y);
    return sign(val) * mod(1000.0 * fract(val * 43758.5453123) + y, OFFSETY);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = fragCoord / iResolution.xy;

    float f1 = iResolution.y * (1.0 - iChannelTime[0] / VIDEOLENGTH); 

    if (fragCoord.y > f1 - OFFSETY && fragCoord.y <= f1 && fragCoord.x > OFFSETX && fragCoord.x < iResolution.x - OFFSETX) {
        float offset = random(fragCoord.y);
        xy.x = (fragCoord.x + offset) / iResolution.x;
    }

    fragColor = texture(iChannel0, xy);
}
