void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = 0.2 * (2. * u - Res) / Res.y;  
    float y0 = exp((-abs(U.y - U.x) + 0.1)/0.01);
    float y1 = exp((-abs(U.y + U.x) + 0.1)/0.01);
    O = vec4(sin(y0 * y1 + iTime/3.), sin(y0 * y1 + iTime/4.), sin(y0 * y1 + iTime/5.), 1.);
}
