vec2 Ball1 = vec2(0.5);
vec2 Ball2 = vec2(0.5);
vec2 Ball3 = vec2(0.5);
vec2 Ball4 = vec2(0.5);
float radio = 0.02;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Change position over time
    Ball1 = 0.5 + 0.1*cos(iTime+uv.xy+vec2(2));
    Ball2 = 0.5 + 0.2*cos(iTime+uv.xy+vec2(2));
    Ball3 = 0.5 + 0.3*cos(iTime+uv.xy+vec2(2));
    Ball4 = 0.5 + 0.4*cos(iTime+uv.xy+vec2(2));


    // Calculate distances of the pixel to the ball position
    float d1 = distance(uv, Ball1);
    float d2 = distance(uv, Ball2);
    float d3 = distance(uv, Ball3);
    float d4 = distance(uv, Ball4);

    // Sum all distances
    vec2 sum = vec2(radio/d1 + radio/d2 + radio/d3 + radio/d4);

    vec2 inf = vec2(0.3);
    // Get mouse Position
    // inf = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);

    vec3 col = vec3(sum.x*inf.x, sum.y*inf.y, inf.x*inf.y);
    // Output to screen
    fragColor = vec4(col,1.0);
}
