void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2.0/uv.x,4));
 vec3 col2 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2.0,4.0/uv.x));
 vec3 col3 = 0.5 + 0.5*cos(iTime+uv.xyy+vec3(0,2.0,4.0/uv.x));
vec3 col4 = mix(col,col2,cos(iTime));

//vec3 col5 = cos(col);//smoothstep(col,col2,col4);

vec3 col5 = cos(smoothstep(col,col2/2.0,col4/2.0));//;

    // Output to screen
    fragColor = vec4(col5,1.0);
}
