vec2 GetFlowVelocity(vec2 pos)
{
    float y_size = iResolution.y / iResolution.x;
    float y_dist = abs(pos.y - y_size * 0.5f) / (0.5f * y_size);

    return vec2(1.0f - y_dist * y_dist, 0.0f);
}

UvField GetVelocityField(vec2 pos)
{
    float eps = 1e-2f;
    UvField uv_field;
    uv_field[0].x = GetFlowVelocity(pos).x;
    uv_field[0].yz = vec2(GetFlowVelocity(pos + vec2(eps, 0.0f)).x - uv_field[0].x, GetFlowVelocity(pos + vec2(0.0f, eps)).x - uv_field[0].x) / eps;

    uv_field[1].x = GetFlowVelocity(pos).y;
    uv_field[1].yz = vec2(GetFlowVelocity(pos + vec2(eps, 0.0f)).y - uv_field[1].x, GetFlowVelocity(pos + vec2(0.0f, eps)).y - uv_field[1].x) / eps;
    return uv_field;
}

vec3 GetTotalLight(vec3 pos, vec3 surface_norm, vec3 albedo_color)
{
    float y_size = iResolution.y / iResolution.x;
    vec3 res_light = vec3(0.0f);
    {
        vec3 delta = pos - vec3(vec2(0.75f, y_size * 0.5f), 0.3f);
        res_light += GetPointLightFalloff(delta) * GetLight(normalize(delta), surface_norm, albedo_color) * vec3(1.0f, 0.5f, 0.01f);
    }
    {
        vec3 delta = pos - vec3(vec2(0.25f, y_size * 0.5f), 0.3f);
        res_light += GetPointLightFalloff(delta) * GetLight(normalize(delta), surface_norm, albedo_color) * vec3(0.01f, 0.5f, 1.0f);
    }
    return res_light;
}

#define INTERP_TYPE 2

Field GetFlowField(sampler2D tex, UvField uv_field, UvField flow_field, float phase, float phase_offset, float eps, uint type)
{
    //phase = 0.0f;
    float period = 3.0f;
    InterpNodes2 interp_nodes2 = GetInterpNodes2(phase / period);
    float moment2 = 0.0f;
    Field res_field;
    for(uint node_index = 0u; node_index < 2u; node_index++)
    {
        vec3 node_hash = hash33(vec3(interp_nodes2.seeds[node_index], 0.0f, 0.0f));
        UvField node_uv_field = uv_field - period * flow_field * (2.0f * interp_nodes2.phases[node_index] - 1.0f) - phase_offset * flow_field;
        UvField hashed_uv_field = RotateMoveUvField(node_uv_field, node_hash.xy, node_hash.z * 2.0f * pi);
        Field tex_field = SampleTexField(hashed_uv_field, tex, eps);
        Field weight_field = Field(interp_nodes2.weights[node_index], 0.0f, 0.0f);

        if(type == 0u)
        {
            res_field += tex_field * weight_field.x;
        }
        if(type == 1u)
        {
            weight_field = FieldRampNorm(weight_field, FieldOneMinus(tex_field * 0.9f));
            res_field += FieldMul(tex_field, weight_field);
        }
        if(type == 2u)
        {
            res_field += FieldRampNorm(tex_field, FieldOneMinus(weight_field * 0.5f));
        }
        //moment2 += interp_nodes2.weights[node_index] * interp_nodes2.weights[node_index];
    }
    return res_field;
    //return PreserveVariance(res_field, vec3(textureLod(tex, vec2(0.5f, 0.5f), 10.0f).r, 0.0f, 0.0f), moment2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / max(iResolution.x, iResolution.y);

    float stretch = iMouse.z > 0.5f ? (iMouse.y / iResolution.y * 10.0f) : 5.0f;
    uint type = 0u;
    if(uv.x > 1.0f / 3.0f) type = 1u;
    if(uv.x > 2.0f / 3.0f) type = 2u;
    if(abs(uv.x - 1.0f / 3.0f) < 1e-3f || abs(uv.x - 2.0f / 3.0f) < 1e-3f)
    {
        fragColor = vec4(1.0f);
        return;
    }
    Field res_field = GetFlowField(iChannel0, IdentityUvField(uv * 2.0f), GetVelocityField(uv) * 0.1f, iTime, stretch, 1.0f / 512.0f, type);

    vec3 res_pos = vec3(uv, 0.0f);
    vec3 res_norm = GradToNorm(res_field.yz * 0.1f);

    //vec3 res_light = vec3(res_norm.xy, 0.0f);
    vec3 res_light = GetTotalLight(res_pos, res_norm, vec3(0.1f));

    vec3 srgb_color = pow(res_light, vec3(1.0f / 2.2f));
    fragColor = vec4(srgb_color, 1.0);
}

#define Field vec3
#define UvField mat2x3

Field SampleTexFieldVec2(vec2 uv, sampler2D tex, float eps)
{
    Field res;
    res.x = texture(tex, uv).r;
    //in actual use this is supposed to be precalculted
    float center = textureLod(tex, uv, 0.0f).r;
    res.y = (textureLod(tex, uv + vec2(eps, 0.0f), 0.0f).r - center) / eps;
    res.z = (textureLod(tex, uv + vec2(0.0f, eps), 0.0f).r - center) / eps;
    return res;
}

Field SampleTexField(UvField uv_field, sampler2D tex, float eps)
{
    Field local_field = SampleTexFieldVec2(vec2(uv_field[0].x, uv_field[1].x), tex, eps);
    return Field(local_field.x, uv_field[0].yz * local_field.y + uv_field[1].yz * local_field.z);
    /*vec2 uv = vec2(uv_field[0].x, uv_field[1].x);
    float center_sample = texture(tex, uv).r;
    float x_sample = texture(tex, uv + uv_field[0].yz * eps).r;
    float y_sample = texture(tex, uv + uv_field[1].yz * eps).r;

    return Field(center_sample, (x_sample - center_sample) / eps, (y_sample - center_sample) / eps);*/
}

Field FieldMul(Field field0, Field field1)
{
    return Field(field0.x * field1.x, field0.x * field1.yz + field1.x * field0.yz);
}
Field FieldLinStep(Field value, Field center, Field steepness)
{
    return FieldMul(value, (steepness + Field(1.0f, 0.0f, 0.0f))) - FieldMul(center, steepness);
}
Field FieldMax(Field field0, Field field1)
{
    return field0.x > field1.x ? field0 : field1;
}
Field FieldMin(Field field0, Field field1)
{
    return field0.x < field1.x ? field0 : field1;
}	
Field FieldInverse(Field value)
{
    float inv = 1.0f / value.x;
    return Field(inv, -inv * inv * value.yz);
}
Field FieldInverseSafe(Field value)
{
    float inv = abs(value.x) > 1e-5f ? 1.0f / value.x : 1e5f;
    return Field(inv, -inv * inv * value.yz);
}

Field FieldOneMinus(Field value)
{
    return Field(1.0f - value.x, -value.yz);
}
Field FieldSqr(Field value)
{
    return Field(value.x * value.x, 2.0f * value.x * value.yz);
}

Field FieldRampNormHalf(Field value, Field center)
{
    //https://www.desmos.com/calculator/tpa2t76anf
    return FieldMul(value, FieldInverseSafe(FieldMul(FieldSqr(2.0f * center), FieldOneMinus(value)) + value));
}

Field FieldRampNorm(Field value, Field center)
{
    float eps = 1e-3f;
    center.x = clamp(center.x, eps, 1.0f - eps);
    bool mirror = center.x < 0.5f;
    Field mirrored_value = mirror ? value : FieldOneMinus(value);
    Field mirrored_center = mirror ? center : FieldOneMinus(center);
    Field ramp = FieldRampNormHalf(mirrored_value, mirrored_center);
    return mirror ? ramp : FieldOneMinus(ramp);
}

Field FieldPowerConst(Field value, float p)
{
    float eps = 1e-3f;
    if(value.x < eps) return value;

    Field res;
    res.x = pow(value.x, p);
    res.yz = res.x * p / value.x * value.yz;
    return res;
}

Field FieldGainConst(Field value, float steepness)
{
    float eps = 1e-3f;
    steepness = clamp(steepness, -1.0f + eps, 1.0f - eps);
    float power = (1.0f + steepness) / (1.0f - steepness);
    Field p = 0.5f * FieldPowerConst(2.0f * ((value.x < 0.5f) ? value : FieldOneMinus(value)), power);
    return (value.x < 0.5f) ? p : FieldOneMinus(p);
}

Field FieldDissolve(Field value, Field alpha)
{
    float eps = 1e-2f;
    Field steepness = alpha.x > eps ? FieldInverse(alpha) : Field(1.0f / eps, 0.0f, 0.0f);

    Field res = FieldMul(FieldMin(Field(0.0f, 0.0f, 0.0f), value - Field(1.0f, 0.0f, 0.0f)), steepness) + Field(1.0f, 0.0f, 0.0f);
    res -= FieldMax(Field(0.0f, 0.0f, 0.0f), Field(eps, 0.0f, 0.0f) - alpha) / eps * 2.0f;
    res = res.x < 0.0f ? Field(-1e-5f, 0.0f, 0.0f) : res;
    return res;
}


UvField IdentityUvField(vec2 uv)
{
    UvField res_field;
    res_field[0] = vec3(uv.x, 1.0f, 0.0f);
    res_field[1] = vec3(uv.y, 0.0f, 1.0f);
    return res_field;
}
UvField ApplyTransform(UvField src_uv_field, mat2x2 src_to_dst)
{
    //Eq0: this is how uv's are transformed for stuff like rotation
    //dst.u(x,y) = src_to_dst * src.u(x,y)
    //dst.v(x,y)                src.v(x,y)

    //Eq1: is done by multiplying left and right side of Eq0 by d/dx:
    //d/dx*dst.u(x,y) = src_to_dst * d/dx * src.u(x,y)
    //d/dx*dst.v(x,y)                d/dy * src.v(x,y)

    //Eq2: is done by multiplying left and right side of Eq0 by d/dy:
    //d/dy*dst.u(x,y) = src_to_dst * d/dy * src.u(x,y)
    //d/dy*dst.v(x,y)                d/dy * src.v(x,y)

    //Eq0, Eq1 and Eq2 can all be written as columns of a matrix with 2 rows and 3 columns:
    //dst.u(x,y) d/dx*dst.u(x,y) d/dy*dst.u(x,y) = T(R) * src.u(x,y) d/dx*src.u(x,y) d/dy*src.u(x,y)
    //dst.v(x,y) d/dx*dst.v(x,y) d/dy*dst.v(x,y)          src.v(x,y) d/dy*src.v(x,y) d/dy*src.v(x,y)

    //dst_uv_mat[2x3] = src_to_dst[2x2] * src_uv_mat[2x3]

    //glsl's mat3x2 has actual size of [2x3] from math's perspective. praise opengl.

    //mat3x2 src_uv_mat = mat3x2(vec2(src_uv_field[0].x, src_uv_field[1].x), vec2(src_uv_field[0].y, src_uv_field[1].y), vec2(src_uv_field[0].z, src_uv_field[1].z));

    //each row of a uv_field is transformed as a vector. so we make it a column (transpose), transform and then transpose the result
    mat3x2 src_uv_mat = transpose(src_uv_field);

    //each single column of src_uv_mat is transformed as a vec2 with src_to_dst matrix to obtain a single column of dst_uv_mat, that is a vec2 as well
    mat3x2 dst_uv_mat = src_to_dst * src_uv_mat;

    UvField res_uv_field;
    //res_uv_field[0] = Field(uv_mat[0].x, uv_mat[1].x, uv_mat[2].x);
    //res_uv_field[1] = Field(uv_mat[0].y, uv_mat[1].y, uv_mat[2].y);
    res_uv_field = transpose(dst_uv_mat);

    return res_uv_field;
}

UvField RotateMoveUvField(UvField src_uv_field, vec2 offset, float ang)
{
    mat2 dst_to_src = mat2(cos(ang), sin(ang), -sin(ang), cos(ang));
    mat2 src_to_dst = transpose(dst_to_src);
    UvField dst_uv_field = ApplyTransform(src_uv_field, src_to_dst);
    dst_uv_field[0].x += offset.x;
    dst_uv_field[1].x += offset.y;
    return dst_uv_field;
}

vec3 GradToNorm(vec2 grad)
{
    return normalize(vec3(-grad, 1.0f));
}

vec2 NormToGrad(vec3 norm)
{
    return -norm.xy / norm.z;
}

float GetDiffuse(vec3 light_dir, vec3 surface_norm)
{
    return max(dot(-light_dir, surface_norm), 0.0f);
}

float GetPointLightFalloff(vec3 delta)
{
    return 1.0f / (dot(delta, delta) + 1e-5f);
}

vec3 GetLight(vec3 light_dir, vec3 surface_norm, vec3 albedo_color)
{
    return GetDiffuse(light_dir, surface_norm) * albedo_color;
}

float sqr(float val) {return val * val;}

struct InterpNodes2
{
    vec2 seeds;
    vec2 phases;
    vec2 weights;
};
InterpNodes2 GetInterpNodes2(float phase)
{
    vec2 global_phases = vec2(phase * 0.5) + vec2(0.5, 0.0);
    vec2 phases = fract(global_phases);
    vec2 seeds = floor(global_phases) * 2.0 + vec2(0.0, 1.0);
    vec2 weights = min(phases, vec2(1.0f) - phases) * 2.0;
    return InterpNodes2(seeds, phases, weights);
}

//from Qizhi Yu, et al [2011]. Lagrangian Texture Advection: Preserving Both Spectrum and Velocity Field. 
//IEEE Transactions on Visualization and Computer Graphics 17, 11 (2011), 1612â€“1623
vec3 PreserveVariance(vec3 linearColor, vec3 meanColor, float moment2)
{
    return (linearColor - meanColor) / sqrt(moment2) + meanColor;
}


vec3 hash33( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return fract(sin(p)*43758.5453123);
}

