float drawCircle(float r, vec2 center, vec2 st, float N)
{
    //return smoothstep(r-0.01, r, distance(center, st));
    return smoothstep(0., N*1.5/iResolution.y, distance(center, st)-r);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.y;

    float N = 8.;
    st *= N;
    float row = floor(st.y);
    float column = floor(st.x);

    //st.x += (mod(row, 2.0)*2.-1.)*smoothstep(0., 1., sin(iTime));
    //st.y += (mod(column, 2.0)*2.-1.)*smoothstep(0., 1., -sin(iTime));
    st += (mod(floor(st.yx), 2.)*2.-1.) * smoothstep(0., 1., sin(iTime)*vec2(1,-1));

    st = fract(st);

    vec3 col = vec3(drawCircle(0.3, vec2(.5), st, N));

    fragColor = vec4(col,1.0);
}
