float mix2(vec2 uv, float a, float b, float c, float d) {
	return mix(mix(a, b, uv.x), mix(c, d, uv.x), uv.y);
}

float hash12(vec2 p)
{
	vec3 p3  = fract(p.xyx * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float noise(vec2 uv, vec2 period, vec2 seed) {
	vec2 quv = floor(uv / period) + seed;
	vec2 fuv = mod(uv, period) / period;
	float a = hash12(quv);
	float b = hash12(quv + vec2(1.,0.));
	float c = hash12(quv + vec2(0., 1.));
	float d = hash12(quv + vec2(1.,1.));

	return mix2(smoothstep(0.,1.,fuv), a, b, c, d);

	return a;

}

float sq(float a) {
	return a*a;
}



vec2 circ_inv(vec2 uv, vec2 center, float radius) {
	float d = distance(uv, center);
	if (d < radius) {
		return uv;
	} else {
		float scale = sq(radius / d);
		return (uv - center) * scale + center;
	}
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord- 0.5*iResolution.xy)/iResolution.y*6.;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    uv = circ_inv(uv, vec2(0.,0.), 1.5);

    uv = sin(uv + 0.2*iTime);

    vec3 col = vec3(noise(uv, vec2(.05, .07), vec2(0.17234, 0.13734)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
