void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 p = fragCoord/iResolution.xy-vec2(0.5,0.5);
	float r=length(p);
	float ang=atan(p.x,p.y)*10./2./3.14;
	int i=int(ang-10.*r-iTime+0.3*iTime);
	int j=int(ang+10.*r+iTime+0.3*iTime);
	fragColor = ((i!=i/2*2)==(j!=j/2*2))?vec4(1,0,0,1):vec4(0,1,0,1);
}
