#define R                           iResolution
#define TO_CENTERED_NDC(SC)       ( ((SC)+(SC)-R.xy) / R.y )         // in range x=[-AR; AR], y=[-1.0; 1.0]
#define UNIT                      (  10.0 / R.y  )
#define SMAA(x)                   ( smoothstep(UNIT, 0., x) )

float vertical_line(vec2 NDC, float x0) { return SMAA(abs(NDC.x-x0)); }
float horizontal_line(vec2 NDC, float y0) { return SMAA(abs(NDC.y-y0)); }
float y_axis(vec2 NDC) { return SMAA(abs(NDC.x)); }
float x_axis(vec2 NDC) { return SMAA(abs(NDC.y)); }
float draw_explicit_func(vec2 NDC, float fx, float dfdx)
{
    float signed_dist = NDC.y - fx;
    return SMAA(abs(signed_dist) / sqrt(1.0 + dfdx * dfdx));
}

vec2 DFDT(float t, vec2 P0, vec2 P1, vec2 P2)
{
    return 2.*(t*(P0-2.*P1+P2)+(P1-P0));
}

float quadraticBezierApproxSDF_L2(vec2 NDC, vec2 P0, vec2 P1, vec2 P2)
{
    int count = 3;
    vec2 B = P0, E;
    float dt_ = 1. / float(count), t_ = 0., min_dist = 99.;

    for (int i = 0; i < count; ++i)
    {
        t_ = float(i+1) * dt_;

        E = quadraticBezier(t_, P0, P1, P2);
        min_dist = min(min_dist, segmentSDF_L2(NDC, B, E));

        B = E;
    }

    return min_dist;
}

/* Simple approxiamtion by rectangles rule (worst result) */
float arclength0(float t, vec2 P0, vec2 P1, vec2 P2)
{
    int count = 10;
    float dx = t / float(count), sum = 0., x = 0.;

    for (int i = 0; i < count; ++i)
    {
        x = float(i) * dx;
        sum += length(DFDT(x, P0, P1, P2));      
    }

    return sum*dx;
}
/* Another simple appriximation by line segments. *
float arclength1(float t, vec2 P0, vec2 P1, vec2 P2)
{
    int count = 40;
    vec2 B = P0, E;
    float dt_ = t / float(count), t_ = 0., sum = 0.;

    for (int i = 0; i < count; ++i)
    {
        t_ = float(i+1) * dt_;

        E = quadraticBezier(t_, P0, P1, P2);
        sum += length(E - B);

        B = E;
    }

    return sum;
}
/* Approximation by Gauss-Legendre quadrature (best result).
   https://www.shadertoy.com/view/st3yzs */
float arclength1(float t, vec2 P0, vec2 P1, vec2 P2)
{
    const float xs[5] = float[5](
        -0.9061798459386639927976,
        -0.5384693101056830910363,
        0.,
        0.5384693101056830910363,
        0.9061798459386639927976  );
    const float ws[5] = float[5](
        0.2369268850561890875143,
        0.4786286704993664680413,
        0.5688888888888888888889,
        0.4786286704993664680413,
        0.2369268850561890875143 );

    float a = 0., b = 1.;
    float hwidth = t*(b-a)*0.5,
          mean = t*(b+a)*0.5;

    float I = 0.;
    for (int i = 0; i < 5; ++i)
    {
        float x = mean + hwidth * xs[i];
              I += length(DFDT(x, P0, P1, P2)*ws[i]);
    }

    return I * mean;
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = TO_CENTERED_NDC(SC) * 2.5;

    vec3 color = vec3(1.);
         color -= x_axis(NDC); color -= y_axis(NDC);

    vec2 P0 = vec2(-2., -1.), P1 = vec2(0.0, 1.0), P2 = vec2(2., -1.);
    float offs = sinOSC(-0.5, 0.5, iTime * 0.5);

    P0 = rotateAroundZ(P0, iTime * 0.1) + vec2(2.*offs, -offs);
    P1 = rotateAroundZ(P1, iTime * 0.9) + vec2(-6.*offs, 0.0);
    P2 = rotateAroundZ(P2, iTime * 0.1) + vec2(-offs, offs);

         color = mix(color, vec3(0, 1, 0), SMAA( quadraticBezierSegmentSDF_L2(NDC, P0, P1, P2) ));
         color = mix(color, vec3(.4, .4, .4), SMAA( quadraticBezierApproxSDF_L2(NDC, P0, P1, P2) ));
         color = mix(color, vec3(0, 0, 1), SMAA( segmentSDF_L2(NDC, P0, P1) ));
         color = mix(color, vec3(0, 0, 1), SMAA( segmentSDF_L2(NDC, P1, P2) ));
         color = mix(color, vec3(1, 0, 1), SMAA( diskSDF_L2(NDC, P0, UNIT) ));
         color = mix(color, vec3(1, 0, 1), SMAA( diskSDF_L2(NDC, P1, UNIT) ));
         color = mix(color, vec3(1, 0, 1), SMAA( diskSDF_L2(NDC, P2, UNIT) ));

#define SCALE 0.25
         float t = fract(iTime * 0.25);
         vec2 P = quadraticBezier(t, P0, P1, P2);
         color = mix(color, vec3(1, 0, 0), SMAA( diskSDF_L2(NDC, P, UNIT) ));
              P = vec2(t, SCALE*arclength0(t, P0, P1, P2));
         color = mix(color, vec3(1, 0, 0), SMAA( diskSDF_L2(NDC, P, UNIT) ));

         t = clamp(NDC.x, 0., 1.);
#define func(x) ( arclength0(x, P0, P1, P2) )
#define dfdx(x, dx) ( ( func((x)+(dx)) - func(x) ) / (dx) )
         color.rb -= 
             draw_explicit_func(
                 NDC, 
                 SCALE * func(t),
                 NDC.x < 0. || NDC.x > 1. ? 0. : SCALE * dfdx(t, 0.01));
#define func2(x) ( arclength1(x, P0, P1, P2) )
#define dfdx2(x, dx) ( ( func2((x)+(dx)) - func2(x) ) / (dx) )
         color.gb -= 
             draw_explicit_func(
                 NDC, 
                 SCALE * func2(t),
                 NDC.x < 0. || NDC.x > 1. ? 0. : SCALE * dfdx2(t, 0.01));

    O = vec4(color,1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}

vec2 rotateAroundZ(vec2 p, float a)
{
    float s = sin(a);
    float c = cos(a);

    return vec2(
        p.x * c - p.y * s,
        p.y * c + p.x * s);
}

float segmentSDF_L2(
    in vec2 NDC,
    in vec2 B,   
    in vec2 E)
{
    vec2 BP = NDC - B, BE = E - B;
    return length(BP - BE * clamp(dot(BP, BE) / dot(BE, BE), 0.0, 1.0));
}

/*
    Return value:
        0 - two complex roots;
        1 - one real root, but root[0] == root[1];
        2 - two real roots;
*/
int solveQuadratic(
    inout vec2[2] roots,
    in float[3] coefs)
{
    float discriminant = coefs[1] * coefs[1] - 4.0 * coefs[0] * coefs[2];

    if (discriminant < 0.0)
    {
        float INVa = 1.0 / (2. * coefs[0]);
        float SQRT = sqrt(-discriminant);

        roots[0].x = -coefs[1] * INVa;
        roots[0].y = SQRT * INVa;
        roots[1].x = -coefs[1] * INVa;
        roots[1].y = -SQRT * INVa;

        return 0;
    }

    if (discriminant == 0.0)
    {
        float Re = -coefs[1] / (2. * coefs[0]);

        roots[0].x = Re;
        roots[0].y = 0.0;
        roots[1].x = Re;
        roots[1].y = 0.0;

        return 1;
    }

    float INVa = 1.0 / (2. * coefs[0]);
    float SQRT = sqrt(discriminant);

    roots[0].x = (SQRT - coefs[1]) * INVa;
    roots[0].y = 0.0;
    roots[1].x = (-coefs[1] - SQRT) * INVa;
    roots[1].y = 0.0;

    return 2;
}

/*
    Return value:
        1 - one real, two complex roots;
        2 - three real roots, but root[1] == root[2];
        3 - three real roots.
*/
int solveCubicVieta(
    inout vec2[3] roots,
    in float[4] coef)
{
    const float oneThird = 0.33333333333333331;
    const float TAU = 6.2831853071795862;

    float a = coef[1] / coef[0];
    float b = coef[2] / coef[0];
    float c = coef[3] / coef[0];

    float Q = (a * a - 3.0 * b) / 9.0;
    float R = (2.0 * a * a * a - 9.0 * a * b + 27.0 * c) / 54.0;

    float S = Q * Q * Q - R * R;

    if (S > 0.0)
    {
        float phi = oneThird * acos(R / sqrt(Q * Q * Q));
        float Re = -2.0 * sqrt(Q);

        roots[0].x = Re * cos(phi) - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re * cos(phi + TAU * oneThird) - a * oneThird;
        roots[1].y = 0.0;
        roots[2].x = Re * cos(phi - TAU * oneThird) - a * oneThird;
        roots[2].y = 0.0;

        return 3;
    }

    if (S < 0.0 && Q > 0.0)
    {
        float phi = oneThird * acosh(abs(R) / sqrt(Q * Q * Q));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(Q);
        float Re = signR * sqrtQ * cosh(phi);
        float Im = sqrtQ * sinh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (S < 0.0 && Q < 0.0)
    {
        float phi = oneThird * asinh(abs(R) / sqrt(abs(Q * Q * Q)));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(abs(Q));
        float Re = signR * sqrtQ * sinh(phi);
        float Im = sqrtQ * cosh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (Q == 0.0)
    {
        //float Re = -cubeRoot(c - a * a * a / 27.0) - a / 3.0;
        float Re = -pow(c - a * a * a / 27.0, 1./3.) - a / 3.0;
        float Im = sqrt(abs(a - 3.0 * Re) * (a + Re) - 4.0 * b) * 0.5;

        roots[0].x = Re;
        roots[0].y = 0.0;
        roots[1].x = -(a + Re) * 0.5;
        roots[1].y = Im;
        roots[2].x = -(a + Re) * 0.5;
        roots[2].y = Im;

        return 1;
    }

    float signR = R < 0.0 ? -1.0 : 1.0;
    float Re = signR * sqrt(Q);

    roots[0].x = -2.0 * Re - a * oneThird;
    roots[0].y = 0.0;
    roots[1].x = Re - a * oneThird;
    roots[1].y = 0.0;
    roots[2] = roots[1];

    return 2;
}

/*
    Returns number of finded real roots.
*/
int solve(
    inout vec2[3] roots,
    in float[4] coef)
{
    // Quadratic equation?
    if (coef[0] == 0.0)
    {
        // Linear equation?
        if (coef[1] == 0.0)
        {
            // Constant?
            if (coef[2] == 0.0) { return 0; }

            // Solve linear.
            roots[0].x = -coef[3]/coef[2];
            roots[0].y = 0.0;

            return 1;
        }

        vec2 r[2];
        float c[3] = float[3](
            coef[1],
            coef[2],
            coef[3]
        );

        int num = solveQuadratic(r, c);
        roots[0] = r[0];
        roots[1] = r[1];

        return num;
    }

    return solveCubicVieta(roots, coef);
}

vec2 quadraticBezier(float t, vec2 P0, vec2 P1, vec2 P2)
{
    return (P0*(1.-t)*(1.-t)+2.*P1*t*(1.-t)+P2*t*t);
}

/* Solution from https://www.desmos.com/calculator/zqj0ztakig */
float quadraticBezierSegmentSDF_L2(
    vec2 NDC,
    vec2 P0,
    vec2 P1,
    vec2 P2)
{
    vec2 C = P2 - 2.*P1 + P0;
    vec2 D = 2.*(P1 - P0);
    vec2 E = P0 - NDC;

    float coef[4] = float[4](
        2. * dot(C, C),
        3. * dot(C, D),
        2. * dot(C, E) + dot(D, D),
        dot(D, E)
    );

    vec2 T[3];
    float minSqDist = 99.;
    int num = solve(T, coef);

    for (int i = 0; i < num; ++i)
    {
        float t = clamp(T[i].x, 0.0, 1.0);
        vec2 diff = quadraticBezier(t, P0, P1, P2) - NDC;

        minSqDist = min(minSqDist, dot(diff, diff));
    }

    return sqrt(minSqDist);
}

float diskSDF_L2(vec2 NDC, vec2 C, float R)
{
    return length(C - NDC)-R;
}
