void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 U =( 2.*fragCoord-iResolution.xy)/iResolution.y;

     float b=sqrt(length(U));

  float t=iTime*.5;U=vec2(b-t,abs(atan(U.x,U.y)));

  vec2 c=U*vec2(50.,25.);

  U=floor(mod(abs(c),vec2(256)));

  float d=b*mod((U.x*U.x+U.y*U.y)*floor(iTime*6.5/4.),256.)/256.;

  fragColor=vec4(fract(10.*d),fract(d*d*2.),d,1.);


}

//double spaced so it looks like more work

float song(float n, float t, float d, float off){

  n=mod(n*n*(off+floor(t/(2.*d))*4.),d);

  n+=mod(floor(t/d),2.)*5.;

  return pow(2.,n/12.+6.);

}

vec2 mainSound(int samp, float time){

  float d=64.,t=time*6.5,a=sqrt(1.-fract(t));

  t=floor(t);

  float n=mod(t,d),nb=song(n,t,d,4.),nl=mod(t-1.,d);

  n=song(n,t,d,3.);nl=song(nl,t,d,3.);

  return vec2((fract(time*n*.998)-.5)*(.2-.1*a)+

   (fract(time*nl*.998)-.5)*.1*a

   ,(fract(time*nb)-.5)*.2*a);

}
