float Circle(vec2 uv, vec2 p, float r, float blur){

     float d = length(uv-p); // Distance function built-in
     float c = smoothstep(r, r-blur, d);

     return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord -.5* iResolution.xy ) / iResolution.y;

    vec2 p = vec2(0.,0.);

    float c = Circle(uv, p, .4, 0.005);

    c -= Circle(uv, vec2(-.12,.23), 0.065,0.005);
    c -= Circle(uv, vec2(.12,.23), 0.065,0.005);

    float mouthR = 0.2;
    float mouth = Circle(uv, vec2(.0,-.05), mouthR, 0.01);
    mouth -=  Circle(uv, vec2(.0,-.01), mouthR, 0.01);

    c-=mouth;
    vec3 col = vec3(1., 1., 0.);
    col *= c;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
