#define pi 3.141592653589793

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = 2.*(fragCoord-iResolution.xy*.5)/iResolution.y;
    vec3 col, col1, col2;

        col1 = vec3(cos(pi*uv.x*8.-iTime)); //Initial waves
        vec2 a = vec2(uv.x,uv.y-0.5);
        vec2 b = vec2(uv.x,uv.y+0.5);
        float c = dot(a,a);
        float d = dot(b,b);
        col2 = vec3(cos(pi*sqrt(c)*8.-iTime)*exp(-c*.4)+cos(pi*sqrt(d)*8.-iTime)*exp(-d*.4))*.5; //Interference

    col = mix(col1,col2,smoothstep(0.,1.,uv.x*15.)); //Interpolate
    col *= min(smoothstep(0.,1.,abs(uv.x)*16.)/smoothstep(0.,1.,abs(abs(uv.y)-0.5)*20.),1.); //Barrier

    col = col*col; //PDF

    col = vec3((-cos(pi*(col.r))/2.+.5),col.r,(1.-(acos(2.*col.r-1.))/pi)); //color

    fragColor = vec4(col,1.0);
}
