#define myPi 3.141592654f
#define Eps 1e-8
#define maxZ 100.0
#define maxVert 8

const int n = 5;
vec3 colVec[n] = vec3[n](vec3(0.5, 0.9, 0.3), vec3(0.1, 0.7, 0.2), vec3(0.4, 0.8, 0.4), vec3(0.5, 1.0, 0.5), vec3(1.0, 0.7, 0.0));
vec3 BGcol = vec3(0.3, 0.9, 0.3);
//float zVec[n] = float[n](3.0, 2.0, 1.0);

int sizes[n] = int[n](3, 5, 4, 3, 10), inds[n] = int[n](0, 3, 8, 12, 15); const int m = 25;
vec2 polygonsXY[m]; float polygonsZ[m];

void initPolygons(out vec2 polygonsXY[m], out float  polygonsZ[m], in float time){
// 0 -- triangle
    polygonsXY[0] = vec2(0.5, 0.0); polygonsZ[0] = 0.1; 
    polygonsXY[2] = vec2(0.6, 0.8); polygonsZ[2] = 0.1;
    polygonsXY[1] = vec2(0.9, 0.9); polygonsZ[1] = 0.1;

// 1 -- pentagon
    polygonsXY[3] = vec2(0.27, 0.05); polygonsZ[3] = 0.1;
    polygonsXY[4] = vec2(0.7, 0.1); polygonsZ[4] = 0.1;
    polygonsXY[5] = vec2(0.83, 0.7); polygonsZ[5] = 0.1;
    polygonsXY[6] = vec2(0.5, 0.8); polygonsZ[6] = 0.1;
    polygonsXY[7] = vec2(0.31, 0.63); polygonsZ[7] = 0.1;

// 2 -- rotated square
    float w = 2.0;
    vec3 center = vec3(0.55, 0.5, 0.1 + 0.02 * sin(time*w)); float r = 0.4;
    int i; float j =0.0, phi = myPi/6.0;
    float ang = 2.0*myPi/4.0; 
    vec3 tmpVec;
    mat3 rot = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(phi), sin(phi)), vec3(0.0, -sin(phi), cos(phi)) );
    for (i = 8; i < 12; i++){
        tmpVec = rot*(r*vec3(cos(j*ang + time), sin(j*ang + time), 0.0)); 
        polygonsXY[i] = tmpVec.xy + center.xy;
        polygonsZ[i] = tmpVec.z + center.z;
        j = j + 1.0;
    }
// 3 -- triangle
    polygonsXY[12] = vec2(0.1, 0.9); polygonsZ[12] = 0.2;
    polygonsXY[13] = vec2(0.05, 0.1); polygonsZ[13] = 0.2;
    polygonsXY[14] = tmpVec.xy + center.xy; polygonsZ[14] = 0.2;

// 4 -- 10tagon
    w = 3.5;
    center = vec3(0.5 + 0.2 * sin(time * w), 0.5 + 0.2 * cos(time), 0.5 + 0.2 * sin(time)); r = 0.4;
    j = 0.0, phi = myPi/10.0;
    ang = 2.0*myPi/10.0; 
    tmpVec;
    rot = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(phi), sin(phi)), vec3(0.0, -sin(phi), cos(phi)));
    for (i = 15; i < 25; i++){
        tmpVec = rot*(r*vec3(cos(j*ang + time), sin(j*ang + time), 0.0)); 
        polygonsXY[i] = tmpVec.xy + center.xy;
        polygonsZ[i] = tmpVec.z + center.z;
        j = j + 1.0;
    }
}



int leftTurn(in vec2 a, in vec2 b, in vec2 c)
{
    float tmp = (c.x - a.x)*(b.y - a.y) - (c.y - a.y)*(b.x - a.x);
    if (abs(tmp) < Eps) 
        return 0;
    else 
        return tmp > 0.0 ? 1: -1;
}


bool inPolygon(in int ind0, in int size, in vec2 vert[m], in vec2 p) // vertices in counter clockwise order
{
    bool res = leftTurn(vert[ind0 + size-1], vert[ind0], p) <= 0; int i = ind0;
    while(res && (i < ind0 + size -1))
    {
        res = res && (leftTurn(vert[i], vert[i+1], p) <= 0); i++;
    }
    return res;
}

float zInPlane(in vec3 a, in vec3 b, in vec3 c, in vec2 p) // z coordinate for (x,y) point of a plane (a-b, c-b)
{
    vec3 nrm = cross(a-b, c-b);
    //float D = - dot(nrm, b);
    if (abs(nrm.z) < Eps) return a.z;
    else return (dot(nrm, b) - nrm.x*p.x - nrm.y*p.y)/nrm.z;
}


// vec3 normalize(vec3), vec3 cross(vec3, vec3) are predefined

void initColors(float time) {
    float w = 2.0;
    vec3 center = vec3(0.7 + 0.2 * sin(time * w), 0.5 + 0.2 * cos(time), 0.2 * sin(time));
    colVec[4] = vec3(center);
    colVec[3] = vec3(sin(time * w), 1.0, 0.5);
    colVec[2] = vec3(cos(time * w), 0.8, 0.4);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime, tmp; //iTime;
    initPolygons(polygonsXY, polygonsZ, time);
    initColors(time);
    vec3 col = BGcol; float z = maxZ; 
    int i;
    for (i = 0; i < n; i++){
        if ( inPolygon(inds[i], sizes[i], polygonsXY, uv) ){
            tmp = zInPlane(vec3(polygonsXY[inds[i]].x, polygonsXY[inds[i]].y, polygonsZ[inds[i]]), \
            vec3(polygonsXY[inds[i]+1].x, polygonsXY[inds[i]+1].y, polygonsZ[inds[i]+1]), \
            vec3(polygonsXY[inds[i]+2].x, polygonsXY[inds[i]+2].y, polygonsZ[inds[i]+2]), uv);
            if (tmp < z){
                z = tmp; col = colVec[i]; 
            }
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
