void mainImage( out vec4 O, vec2 I ) {
    float N = 10.;
    vec2 uv = (I.xy / iResolution.xy);
    int tx = int(uv.y);
    float react = texelFetch( iChannel0, ivec2(tx,0), 0 ).x*0.05;
    I = (I+I-(O.xy=iResolution.xy))/O.y;
    float r = length(I);
    float a = atan(I.y,I.x);
    a -= 100.*(react+2.+.1*iTime)-100.*r;
    r +=  (0.5+sin(a+.15*react)/.15*cos(a))/ N-react ;    
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,3,6));
	O = (1.-r)*vec4(col*0.1+30.*react*col,1.);
}
