float Band(float t, float start, float end, float blur){
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);

    return step1*step2;

}
float Circle(vec2 uv, vec2 p, float r, float blur){

    float d = length(uv-p);
    float c = smoothstep( r, r-blur, d);

    return c;
}

float Smiley(vec2 uv, vec2 p, float size){
    uv -= p;
    uv /= size;
    float mask = Circle(uv, vec2(0.), .4, .005);

    mask -= Circle(uv, vec2(.13, .2), .07, .01);
    mask -= Circle(uv, vec2(-.13, .2), .07, .01);

    float mouth = Circle( uv , vec2(0.), .25, .009);
    mouth -= Circle( uv , vec2(0.,0.1), .25, .009);

    mask -= clamp(mouth, 0., 1.);

    return mask;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur){
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);

    return band1*band2;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    float mask = Smiley(uv, vec2(0.), 1.1);

    mask = Rect(uv, -.3, .3, -.2, .2, 0.005);

    col = vec3(.5, .2, 1.)*mask;

    fragColor = vec4(col,1.0);
}
