const float FLOW_DISTANCE = 0.2;
const float FLOW_SPEED = 0.15;

float saturate(float x)
{
    return clamp(x, 0.0, 1.0);
}

vec2 flowmap(in vec2 uv)
{
    vec2 dir = uv - vec2(0.5, 0.5);
    vec2 flow = normalize(vec2(dir.y, -dir.x));

    flow *= saturate(1.0 - 4.0*abs(0.25 - length(dir))) * 1.0;  // circular swirly motion
    flow = mix(vec2(0.0, 0.5), flow, saturate(length(flow)*2.0));  // linear movement up

    return flow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 flow = -flowmap(uv);

    float phase0 = fract(iTime*FLOW_SPEED + 0.5);
    float phase1 = fract(iTime*FLOW_SPEED + 1.0);

    vec2 f0 = flow * phase0 * FLOW_DISTANCE;
    vec2 f1 = flow * phase1 * FLOW_DISTANCE;

    vec3 s0 = texture(iChannel0, uv + f0).xyz;
    vec3 s1 = texture(iChannel0, uv + f1).xyz;

    float lerp = saturate(abs((0.5 - phase0) / 0.5));

    vec3 col = mix(s0, s1, lerp);

    float anim_separator = max(0.0, sin(iTime*0.5));

    if(uv.x < anim_separator) col = vec3(flow*0.5 + 0.5,0.0);

    fragColor = vec4(col,1.0);
}
